class FlowCreateCommandArgsTestCase(unittest.TestCase):
    """Test createCommandArgs method of Flow"""

    def test_single_sink(self):
        """Test a valid single sink"""
        expected = ["xdd", "-target", "foo1", "-op", "write", "-e2e", "isdest"]
        expected.extend(["-reqsize", "1024", "-bytes", "1024"])
        expected.extend(["-e2e", "dest", "host:40000,1"])
        #expected.extend(["-preallocate", "1024"])
        expected.extend(["-noordering"])
        expected.extend(["-verbose", "-minall", "-stoponerror"])
        #expected.extend(["-stdout", "logfile"])
        expected.extend(["-hb", "2", "-hb", "bytes", "-hb", "lf"])

        flow = xdd.Flow(True, 1024,
                        0, 1, [("host", 40000, 1)],
                        False, False, False, False)
        flow.target = 'foo1'
        flow.dataSize = 1024
        cmd = flow.createCommandArgs()
        i = 0
        for e in expected:
            self.assertEqual(e, cmd[i])
            i += 1
        self.assertEqual(len(expected), len(cmd))

    def test_single_source(self):
        """Test a valid single source"""
        expected = ["dir/xdd", "-target", "foo2", "-op", "read", "-e2e", "issource"]
        expected.extend(["-reqsize", "1024", "-bytes", "1024"])
        expected.extend(["-e2e", "dest", "localhost:40000,1"])
        expected.extend(["-looseordering"])
        expected.extend(["-verbose", "-minall", "-stoponerror"])
        expected.extend(["-hb", "2", "-hb", "bytes", "-hb", "lf"])

        flow = xdd.Flow(False, 1024,
                        0, 1, [("localhost", 40000, 1)],
                        False, False, False, False, 'dir')
        flow.target = 'foo2'
        flow.dataSize = 1024        
        cmd = flow.createCommandArgs()
        i = 0
        for e in expected:
            self.assertEqual(e, cmd[i])
            i += 1
        self.assertEqual(len(expected), len(cmd))

    def test_multiple_sink(self):
        """Test a valid multiple sink"""
        expected = ["bin/xdd", "-target", "foo3", "-op", "write", "-e2e", "isdest"]
        expected.extend(["-reqsize", "1024", "-bytes", "1024"])
        expected.extend(["-e2e", "dest", "if1:40000,1"])
        expected.extend(["-e2e", "dest", "if2:40000,1"])
        #expected.extend(["-preallocate", "1024"])
        expected.extend(["-noordering"])
        expected.extend(["-verbose", "-minall", "-stoponerror"])
        #expected.extend(["-stdout", "logfile"])
        expected.extend(["-hb", "2", "-hb", "bytes", "-hb", "lf"])

        flow = xdd.Flow(True, 1024,
                        0, 1, [("if1", 40000, 1), ("if2", 40000, 1)],
                        False, False, False, False, 'bin')
        flow.target = 'foo3'
        flow.dataSize = 1024
        cmd = flow.createCommandArgs()
        i = 0
        for e in expected:
            self.assertEqual(e, cmd[i])
            i += 1
        self.assertEqual(len(expected), len(cmd))

    def test_multiple_source(self):
        """Test a valid multiple source"""
        expected = ["xdd", "-target", "foo4", "-op", "read", "-e2e", "issource"]
        expected.extend(["-reqsize", "1024", "-bytes", "1024"])
        expected.extend(["-e2e", "dest", "if1:40000,1"])
        expected.extend(["-e2e", "dest", "if2:40000,1"])
        expected.extend(["-looseordering"])
        expected.extend(["-verbose", "-minall", "-stoponerror"])
        expected.extend(["-hb", "2", "-hb", "bytes", "-hb", "lf"])

        flow = xdd.Flow(False, 1024, 
                        0, 1, [("if1", 40000, 1), ("if2", 40000, 1)],
                        False, False, False, False)
        flow.target = 'foo4'
        flow.dataSize = 1024
        cmd = flow.createCommandArgs()
        i = 0
        for e in expected:
            self.assertEqual(e, cmd[i])
            i += 1
        self.assertEqual(len(expected), len(cmd))

class FlowStartTestCase(unittest.TestCase):
    """Test start method of Flow"""

    def test_zero_to_null(self):
        """Start a zero to null flow"""
        sink = xdd.Flow(True, 1024, 
                        0, 1, [("localhost", 40000, 1), ("localhost", 40010, 1)],
                        False, False, False, False, 'bin')
        sink.start('/dev/null', 4096)

        source = xdd.Flow(False, 1024, 
                        0, 1, [("localhost", 40000, 1), ("localhost", 40010, 1)],
                        False, False, False, False, 'bin')
        source.start('/dev/zero', 4096)

        import time
        while sink.completionStatus() is None:
            time.sleep(3)
        self.assertEqual(sink.completionStatus(), 0)
        self.assertEqual(source.completionStatus(), 0)

if __name__ == '__main__':
    unittest.main()
